import React, { useState, ChangeEvent, FormEvent } from "react";
import axios from "axios";
import { FormData } from "../../interfaces/sign-up-form/form-data"; // Adjust the import as needed
import environment from "../../environments/environment";
import { useNavigate } from "react-router-dom";
import './sign-up-form.css'; // Update with your actual path

const SignupForm: React.FC = () => {
  const navigate = useNavigate(); // Hook to navigate to a different route

  // State and form handling logic
  const [formData, setFormData] = useState<FormData>({
    email: "",
    name: "",
    password: ""
  });
  const [errors, setErrors] = useState<Partial<FormData>>({});

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e: FormEvent) => {
    e.preventDefault();
    try {
      await axios.post(`${environment.API_URL}/api/signup`, formData);
      // Navigate to the welcome page after successful signup
      navigate("/welcome");
    } catch (err) {
      alert("Error during sign-up");
    }
  };

  const isFormValid = (): boolean => {
    const { name, email, password } = formData;

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const passwordErrors = validatePassword(password);

    return (
      name.trim().length >= 3 &&
      emailRegex.test(email) &&
      passwordErrors.length === 0
    );
  };

  const validatePassword = (value: string): string[] => {
    const errorMessages: string[] = [];

    if (value.length < 8) {
      errorMessages.push("Minimum 8 characters.");
    }
    if (!/[a-zA-Z]/.test(value)) {
      errorMessages.push("At least one letter.");
    }
    if (!/[0-9]/.test(value)) {
      errorMessages.push("At least one number.");
    }
    if (!/[!@#$%^&*(),.?\":{}|<>]/.test(value)) {
      errorMessages.push("At least one special character.");
    }

    return errorMessages;
  };

  return (
    <div className="signup-form-wrapper">
      <h2>Sign Up</h2>
      <form onSubmit={handleSubmit} className="signup-form">
        {/* Email and password fields */}
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
          />
        </div>

        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
          />
        </div>

        <button type="submit" disabled={!isFormValid()}>
          Sign Up
        </button>
      </form>
    </div>
  );
};

export default SignupForm;
