import React, { useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, useLocation, useNavigate } from "react-router-dom";
import SigninForm from "./components/sign-in-form/sign-in-form";
import SignupForm from "./components/sign-up-form/sign-up-form";
import WelcomePage from "./components/welcome-page/welcome-page";
import { ProtectedRoute } from "./guards/protected-route";

function useLockNavigation() {
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    // Replace current entry to reset backstack on every route change
    navigate(location.pathname, { replace: true });

    const handlePopState = () => {
      // Prevent back/forward buttons completely
      navigate(location.pathname, { replace: true });
    };

    window.addEventListener("popstate", handlePopState);
    return () => {
      window.removeEventListener("popstate", handlePopState);
    };
  }, [location, navigate]);
}

const App: React.FC = () => {
  useLockNavigation(); // <— This locks down back/forward navigation globally
  return (
    <Router> {/* Wrap the entire app in Router */}
      <Routes>
        <Route path="/" element={<SigninForm />} />
        <Route path="/signup" element={<SignupForm />} />
        <Route
          path="/welcome"
          element={
            <ProtectedRoute>
              <WelcomePage onLogout={() => {}} />
            </ProtectedRoute>
          }
        />
      </Routes>
    </Router>
  );
};

export default App;
