import React, { useState, ChangeEvent, FormEvent } from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate hook
import axios from "axios";
import { FormData } from "../../interfaces/sign-up-form/form-data";  // Make sure you define the interface for signup form
import environment from "../../environments/environment";
import './sign-up-form.css';

const SignupForm: React.FC = () => {
  const [formData, setFormData] = useState<FormData>({
    email: "",
    password: "",
    confirmPassword: ""
  });
  const [errors, setErrors] = useState<Partial<FormData>>({});
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [signupError, setSignupError] = useState<string | null>(null); // Error state for signup issues

  const navigate = useNavigate();  // Initialize the useNavigate hook for navigation

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    const error = validateField(name, value);
    setErrors({ ...errors, [name]: error });
  };

  const handleSubmit = async (e: FormEvent) => {
    e.preventDefault();
    try {
      // Check if password and confirm password match
      if (formData.password !== formData.confirmPassword) {
        setSignupError("Passwords do not match.");
        return;
      }

      const response = await axios.post(`${environment.API_URL}/users/signup`, formData);

      // If signup is successful and response contains token
      if (response.data.token) {
        // Store the token in localStorage
        localStorage.setItem("token", response.data.token);

        // Redirect to welcome page, ensuring we cannot go back to the signup page
        navigate("/welcome", { replace: true });
      } else {
        // If no token is found, display signup error
        setSignupError("Signup failed. Please try again.");
      }
    } catch (err) {
      console.error('Error signing up:', err);
      setSignupError("An error occurred during signup. Please try again later.");
    }
  };

  const isFormValid = (): boolean => {
    const { email, password, confirmPassword } = formData;
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const passwordErrors = validatePassword(password);
    return emailRegex.test(email) && passwordErrors.length === 0 && password === confirmPassword;
  };

  const validateField = (name: string, value: string) => {
    if (name === "password" || name === "confirmPassword") {
      return validatePassword(value);
    }
    if (name === "email") {
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (!emailRegex.test(value)) {
        return "Please enter a valid email address.";
      }
    }
    return "";
  };

  const validatePassword = (value: string): string[] => {
    const errorMessages: string[] = [];
    if (value.length < 8) errorMessages.push("Minimum 8 characters.");
    if (!/[a-zA-Z]/.test(value)) errorMessages.push("At least one letter.");
    if (!/[0-9]/.test(value)) errorMessages.push("At least one number.");
    if (!/[!@#$%^&*(),.?\":{}|<>]/.test(value)) errorMessages.push("At least one special character.");
    return errorMessages;
  };

  return (
    <div className="signup-form-wrapper">
      <h2 className="signup-title">User Sign Up</h2>
      <form onSubmit={handleSubmit} className="signup-form">
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <div className="input-wrapper">
            <i className="fas fa-envelope"></i>
            <input
              type="email"
              id="email"
              name="email"
              placeholder="Enter your email"
              value={formData.email}
              onChange={handleChange}
              className={errors.email ? "input-error" : ""}
            />
          </div>
          <div className="error-message-space">
            {errors.email && <span className="error">{errors.email}</span>}
          </div>
        </div>

        <div className="form-group">
          <label htmlFor="password">Password</label>
          <div className="input-wrapper">
            <i className="fas fa-lock"></i>
            <input
              type={showPassword ? "text" : "password"}
              id="password"
              name="password"
              placeholder="Enter your password"
              value={formData.password}
              onChange={handleChange}
              className={errors.password?.length ? "input-error" : ""}
            />
            <i
              className={`fas ${showPassword ? "fa-eye-slash" : "fa-eye"} toggle-password`}
              onClick={() => setShowPassword(!showPassword)}
              role="button"
              aria-label="Toggle password visibility"
            ></i>
          </div>
          <div className="error-message-space">
            {Array.isArray(errors.password) && errors.password.map((err, idx) => (
              <div key={idx} className="error">
                {err}
              </div>
            ))}
          </div>
        </div>

        <div className="form-group">
          <label htmlFor="confirmPassword">Confirm Password</label>
          <div className="input-wrapper">
            <i className="fas fa-lock"></i>
            <input
              type={showConfirmPassword ? "text" : "password"}
              id="confirmPassword"
              name="confirmPassword"
              placeholder="Confirm your password"
              value={formData.confirmPassword}
              onChange={handleChange}
              className={errors.confirmPassword?.length ? "input-error" : ""}
            />
            <i
              className={`fas ${showConfirmPassword ? "fa-eye-slash" : "fa-eye"} toggle-password`}
              onClick={() => setShowConfirmPassword(!showConfirmPassword)}
              role="button"
              aria-label="Toggle password visibility"
            ></i>
          </div>
          <div className="error-message-space">
            {Array.isArray(errors.confirmPassword) && errors.confirmPassword.map((err, idx) => (
              <div key={idx} className="error">
                {err}
              </div>
            ))}
          </div>
        </div>

        <button type="submit" disabled={!isFormValid()}>Sign Up</button>

        {/* Show error message if signup fails */}
        {signupError && <div className="error-message">{signupError}</div>}

        {/* Link to navigate to the Sign-In screen */}
        <div className="form-footer">
          <p className="signup-prompt">
            Already have an account?{" "}
            <span
              className="signup-link"
              onClick={() => navigate("/", { replace: true })}
              role="button"
            >
              Sign In
            </span>
          </p>
        </div>
      </form>
    </div>
  );
};

export default SignupForm;
