import React, { useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom"; // Import useNavigate hook
import './welcome-page.css';

const WelcomePage: React.FC<{ onLogout: () => void }> = ({ onLogout }) => {
  const navigate = useNavigate(); // Hook to navigate programmatically

  // Function to handle logout
  const handleLogout = () => {
    // Perform any logout operations here (e.g., clear tokens, user data)
    onLogout(); // Call the onLogout prop function

    // Navigate to login page after logout
    navigate("/", {replace: true});
  };

  const LockHistoryInPlace = () => {
    const location = useLocation();
    const navigate = useNavigate();

    useEffect(() => {
      // Replace the current history entry with the same one
      navigate(location.pathname, { replace: true });

      const handlePopState = () => {
        // When user tries to go back/forward, stay on the current route
        navigate(location.pathname, { replace: true });
      };

      window.addEventListener("popstate", handlePopState);
      return () => {
        window.removeEventListener("popstate", handlePopState);
      };
    }, [location.pathname, navigate]);

    return null;
  };


  return (
    <>
    <LockHistoryInPlace />
    <div className="welcome-page-wrapper">
      <div className="welcome-page">
        <p className="paragraph-style">Welcome to the application.</p>
        <button onClick={handleLogout}>Logout</button>
      </div>
    </div>
    </>
  );
};

export default WelcomePage;
