import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import './welcome-page.css';

const WelcomePage: React.FC<{ onLogout: () => void }> = ({ onLogout }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    // Perform any logout operations here (e.g., clear tokens, user data)
    onLogout(); // Call the onLogout prop function

    // Navigate to the login page after logout, with replace: true to prevent going back to welcome
    navigate("/", { replace: true });
  };

  // Disable back and forward buttons by using pushState on the history
  useEffect(() => {
    // Push a new state to the history stack that prevents going back
    window.history.pushState(null, "", window.location.href);
    window.onpopstate = () => {
      window.history.pushState(null, "", window.location.href);
    };

    // Cleanup on unmount
    return () => {
      window.onpopstate = null; // Remove the popstate listener on component unmount
    };
  }, [navigate]);

  return (
    <div className="welcome-page-wrapper">
      <div className="welcome-page">
        <p className="paragraph-style">Welcome to the application.</p>
        <button onClick={handleLogout}>Logout</button>
      </div>
    </div>
  );
};

export default WelcomePage;
