import React, { useState, ChangeEvent, FormEvent } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import './sign-up-form.css';

const SignupForm: React.FC = () => {
  const navigate = useNavigate(); // Add useNavigate hook to navigate programmatically

  const [formData, setFormData] = useState({ email: "", name: "", password: "" });
  const [errors, setErrors] = useState<Partial<{ name: string; email: string; password: string }>>({});
  const [showPassword, setShowPassword] = useState(false);

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });

    // Validate current field
    const error = validateField(name, value);
    setErrors({ ...errors, [name]: error });
  };

  const handleSubmit = async (e: FormEvent) => {
    e.preventDefault();
    try {
      const response = await axios.post("/api/signup", formData);
      console.log(response.data);
      // Navigate to the Welcome page after successful sign-up and prevent back action
      navigate("/welcome", { replace: true });
    } catch (err) {
      console.error("Error signing up:", err);
    }
  };

  const validateField = (name: string, value: string) => {
    if (name === "password") {
      if (value.length < 8) return "Password must be at least 8 characters long.";
      if (!/[a-zA-Z]/.test(value)) return "Password must contain at least one letter.";
      if (!/[0-9]/.test(value)) return "Password must contain at least one number.";
      if (!/[!@#$%^&*(),.?":{}|<>]/.test(value)) return "Password must contain at least one special character.";
    }
    if (name === "email" && !/\S+@\S+\.\S+/.test(value)) {
      return "Please enter a valid email address.";
    }
    if (name === "name" && value.trim().length < 3) {
      return "Name must be at least 3 characters long.";
    }
    return "";
  };

  return (
    <div className="signup-form-wrapper">
      <h2 className="signup-title">User Sign Up</h2>
      <form onSubmit={handleSubmit} className="signup-form">
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <div className="input-wrapper">
            <i className="fas fa-user"></i>
            <input type="text" id="name" name="name" placeholder="Enter your name" value={formData.name}
              onChange={handleChange} className={errors.name ? "input-error" : ""} />
          </div>
          {errors.name && <div className="error">{errors.name}</div>}
        </div>

        <div className="form-group">
          <label htmlFor="email">Email</label>
          <div className="input-wrapper">
            <i className="fas fa-envelope"></i>
            <input type="email" id="email" name="email" placeholder="Enter your email" value={formData.email}
              onChange={handleChange} className={errors.email ? "input-error" : ""} />
          </div>
          {errors.email && <div className="error">{errors.email}</div>}
        </div>

        <div className="form-group">
          <label htmlFor="password">Password</label>
          <div className="input-wrapper">
            <i className="fas fa-lock"></i>
            <input type={showPassword ? "text" : "password"} id="password" name="password"
              placeholder="Enter your password" value={formData.password} onChange={handleChange} />
            <i className={`fas ${showPassword ? "fa-eye-slash" : "fa-eye"} toggle-password`} onClick={() => setShowPassword(!showPassword)} />
          </div>
          {errors.password && <div className="error">{errors.password}</div>}
        </div>

        <button type="submit" disabled={!formData.email || !formData.password || !formData.name}>Sign Up</button>
        <div className="form-footer">
          <p className="login-prompt">
            Already have an account? <span className="login-link" onClick={() => navigate("/login")}>Login</span>
          </p>
        </div>
      </form>
    </div>
  );
};

export default SignupForm;
